import './App.css';
import Nav from './Nav';
// import Card from "./Card";
import Header from "./Header";
import { useEffect, useState } from 'react';
import axios from 'axios';
import Coin from './Coin';

function App() {

  const[getData,setData] = useState([]);


    useEffect(()=>{
        try{
            async function fetchData(){
                try{
                    const response = await axios.get("https://api.coingecko.com/api/v3/coins/markets?vs_currency=inr&order=market_cap_desc&per_page=100&page=1&sparkline=false");
                    // console.log(response.data);
                    setData(response.data);
                    console.log(response.data);
                }catch(error){
                    console.log(error);
                }
            }
            fetchData();
        }catch(error){
            console.log(error);
        }
    },[]);



  return (
    <div className="App">
      <div className="navigation-bar">
        <Nav />
      </div>
      <div className="content-container">
        <Header />

        {getData && getData.map((currentElement)=>{
          return(
            <div key={currentElement.id}>

              <Coin  

                image={currentElement.image}
                name={currentElement.name}
                currentPrice={currentElement.current_price}
                changeIn={currentElement.price_change_percentage_24h}
              />
            </div>
          );
        })}

      </div>

    </div>
  );
}

export default App;
